mapper.select:
SELECT comment.*, (like1.user_id IS NOT NULL) AS is_liked, COUNT(like2.user_id) AS like_count
FROM comment
         LEFT JOIN
     comment_likes like1 ON comment.id = like1.comment_id AND like1.user_id = #{userId}
         LEFT JOIN
     comment_likes like2 ON comment.id = like2.comment_id
WHERE comment.id = #{id}
GROUP BY comment.id;

SELECT comment.*, (like1.user_id IS NOT NULL) AS is_liked, COUNT(like2.user_id) AS like_count
FROM comment
         LEFT JOIN
     comment_likes like1 ON comment.id = like1.comment_id AND like1.user_id = #{userId}
         LEFT JOIN
     comment_likes like2 ON comment.id = like2.comment_id
WHERE article_id = #{articleId}
GROUP BY comment.id
ORDER BY like_count DESC, comment.id DESC;
